mapper.update:
UPDATE MEDIC_MONITOR_DSA
SET EVALUATE_BRIEF = #{evaluateBrief},
    EVALUATOR_NAME = #{evaluatorName},
    EVALUATOR_ID = #{evaluatorId}
WHERE Id = #{observeId}

UPDATE MEDIC_MONITOR_DSA
SET STATUS = #{status}
WHERE Id = #{observeId}

UPDATE MEDIC_MONITOR_DSA
SET evaluator_id = #{evaluatorId},
evaluator_name = #{evaluatorName}
WHERE Id = #{observeId}

UPDATE MEDIC_MONITOR_DSA
SET quality_evaluate_id = #{qualityEvaluateId},
quality_evaluate_name = #{qualityEvaluateName}
WHERE Id = #{observeId}

mapper.select:
SELECT MEDIC_MONITOR_DSA.id FROM MEDIC_MONITOR_DSA
LEFT JOIN MEDIC_ORGANIZATION
ON MEDIC_ORGANIZATION.id = MEDIC_MONITOR_DSA.report_org_id
WHERE MEDIC_MONITOR_DSA.is_deleted = 'N'
AND MEDIC_MONITOR_DSA.status = '已评价'

    AND MEDIC_MONITOR_DSA.gmt_updated >= #{startDate} and MEDIC_MONITOR_DSA.gmt_updated <= #{endDate}






        AND MEDIC_MONITOR_DSA.has_accident = #{hasAccident}







        AND MEDIC_ORGANIZATION.higher_org = #{orgRange}


ORDER BY RAND() LIMIT #{sampleCount}

SELECT RPT.* ,ORG2.org_name as higher_org_name FROM MEDIC_MONITOR_DSA as RPT, MEDIC_ORGANIZATION as ORG1, MEDIC_ORGANIZATION as ORG2 WHERE RPT.report_org_id = ORG1.id AND ORG1.higher_org = ORG2.id AND RPT.is_deleted = 'N' AND RPT.status = '已评价' AND RPT.quality_status = #{evaluateStatus} AND RPT.id in
#{item}
ORDER BY ID DESC LIMIT #{pageStart},#{pageSize}
